mapper.update:
update caearea
set `name` = #{name,jdbcType=VARCHAR},
  code = #{code,jdbcType=INTEGER},
  provinceId = #{provinceid,jdbcType=VARCHAR}
where areaId = #{areaid,jdbcType=VARCHAR}

mapper.select:
select areaId, `name`, code, provinceId
from caearea
where areaId = #{areaid,jdbcType=VARCHAR}
