mapper.update:
update caebusinessmode
set PageNum = #{pagenum,jdbcType=INTEGER},
  ballotTypeID = #{ballottypeid,jdbcType=VARCHAR},
  ballotName = #{ballotname,jdbcType=VARCHAR}
where busiModeId = #{busimodeid,jdbcType=VARCHAR}

mapper.select:
select busiModeId, PageNum, ballotTypeID, ballotName
from caebusinessmode
where busiModeId = #{busimodeid,jdbcType=VARCHAR}
