mapper.update:
update caecandidatecheckresult
set recepitNumber = #{recepitnumber,jdbcType=VARCHAR},
  `name` = #{name,jdbcType=VARCHAR},
  errorReason = #{errorreason,jdbcType=VARCHAR}
where candidateCheckResultId = #{candidatecheckresultid,jdbcType=VARCHAR}

mapper.select:
select candidateCheckResultId, recepitNumber, `name`, errorReason
from caecandidatecheckresult
where candidateCheckResultId = #{candidatecheckresultid,jdbcType=VARCHAR}
