mapper.update:
update caevotedisplayitem
set sn = #{sn,jdbcType=INTEGER},
  title = #{title,jdbcType=VARCHAR},
  `type` = #{type,jdbcType=VARCHAR},
  voteId = #{voteid,jdbcType=VARCHAR}
where displayItemId = #{displayitemid,jdbcType=VARCHAR}

mapper.select:
